import "./App.css";
import React, { useState } from "react";
import axios from "axios";
import SearchBar from "./components/Search_Bar";
import CurrentWeather from "./components/Current_Weather";
import Everything from "./components/Everything";

function App() {
  const [data, setData] = useState({});
  const [location, setLocation] = useState("");

  const url = `https://api.openweathermap.org/data/2.5/weather?q=${location}&units=imperial&appid=e2836da8f0106826630e93efaa0f31ce`;

  const searchLocation = (event) => {
    if (event.key === "Enter") {
      axios.get(url).then((response) => {
        setData(response.data);
        console.log(response.data);
      });
    }
  };

  return (
    <>
      <SearchBar />
      <body>
        <CurrentWeather />

        <Everything />
      </body>

      <div className="app">
        <div className="search">
          <input
            value={location}
            onChange={(event) => setLocation(event.target.value)}
            onKeyPress={searchLocation}
            placeholder="Enter Location"
            type="text"
          />
        </div>



            <div className="location">
              <p>{data.name}</p>
            </div>
            <div className="temp">
              {data.main ? <h1>{data.main.temp.toFixed()}°F</h1> : null}
            </div>
            <div className="description">
              {data.weather ? <p> {data.weather[0].main}</p> : null}
            </div>


          {data.name != undefined && (
            <div className="bottom">
              <div className="feels">
                {data.main ? (
                  <p className="bold"> {data.main.feels_like}°F</p>
                ) : null}
                <p>Feels like</p>
              </div>
              <div className="hum">
                {data.main ? (
                  <p className="bold"> {data.main.humidity}%</p>
                ) : null}
                <p>Humidity</p>
              </div>
              <div className="wind">
                {data.wind ? (
                  <p className="bold"> {data.wind.speed}MPH</p>
                ) : null}
                <p>Wind Speed</p>
              </div>
            </div>
          )}

      </div>
    </>
  );
}

export default App;
