import { useState } from "react";


function SearchBar({ getWeather }) {
  const [city, setCity] = useState("");
  const getWeatherHandler = () => {
    getWeather(city);
  };
  return (

    <header className="search_bar">
    <div>
      <input
        type="text"
        className="search"
        value={city}
        placeholder="Enter location"
        onChange={(here) => setCity(here.target.value)}
      />
      <button className="button" onClick={getWeatherHandler}>Find Weather</button>
    </div>
    </header>
  );
}
export default SearchBar;
